filter bgp_cull {
    term sourced_by_peeringlan-v4 {
        from {
            source-port 179;
            ip-source-address {
                192.0.2.0/24;
            }
        }
        then {
            count bgp_cull;
            discard;
        }
    }
    term towards_peeringlan-v4 {
        from {
            destination-port 179;
            ip-destination-address {
                192.0.2.0/24;
            }
        }
        then {
            count bgp_cull;
            discard;
        }
    }
    term sourced_by_peeringlan-v6 {
        from {
            source-port 179;
            ip-version {
                ipv6 {
                    ip6-source-address {
                        2001:db8:2::/64;
                    }
                }
            }
        }
        then {
            count bgp_cull;
            discard;
        }
    }
    term towards_peeringlan-v6 {
        from {
            destination-port 179;
            ip-version {
                ipv6 {
                    ip6-destination-address {
                        2001:db8:2::/64;
                    }
                }
            }
        }
        then {
            count bgp_cull;
            discard;
        }
    }
    term rest {
        then accept;
    }
}
